package org.ops4j.pax.repository.example;import javax.inject.Inject;import org.ops4j.pax.repository.Artifact;import org.ops4j.pax.repository.RepositoryException;import org.ops4j.pax.repository.Resolver;import org.ops4j.pax.repository.maven.GAV;import org.ops4j.pax.repository.maven.GavIndex;import org.ops4j.pax.repository.spi.ArtifactEntry;import org.ops4j.pax.repository.spi.Repository;/** * Can translate an arbitrary query to something that the index can understand. */public class System implements Resolver<String>{    @Inject    Repository<ArtifactEntry> repo;    @Inject    GavIndex index;    public Artifact find( String query )        throws RepositoryException    {        GAV gav = null;        return index.get( gav );    }}