package org.ops4j.pax.repository.example;import org.junit.Test;import org.ops4j.pax.repository.RepositoryException;import org.ops4j.pax.repository.base.typed.DefaultTypedRepository;import org.ops4j.pax.repository.typed.TypedRepository;import static junit.framework.Assert.*;/** *  */public class TypedRepositoryDemo {    @Test    public void testTyped()        throws RepositoryException    {        TypedRepository<String> rep = new DefaultTypedRepository<String>();        rep.set( OtherReference.ARTIFACT1, "one" );        rep.set( OtherReference.ARTIFACT2, "two" );        rep.set( YetAnotherReference.ARTIFACT3, "three" );        assertEquals( "one", rep.get( OtherReference.ARTIFACT1 ).get() );        assertEquals( "two", rep.get( OtherReference.ARTIFACT2 ).get() );        assertEquals( "three", rep.get( YetAnotherReference.ARTIFACT3 ).get() );    }}