package org.ops4j.pax.repository.typed;import org.ops4j.pax.repository.Provider;import org.ops4j.pax.repository.RepositoryException;/** * Mark a repository for exposing also future elements. * That is, technically changing values for given keys. */public interface TypedRepository<V> {    TypedReference set( TypedReference key, V value ) throws RepositoryException;    Provider<V> get (TypedReference key)        throws RepositoryException;    }