package org.ops4j.base.io;import java.io.IOException;import java.io.InputStream;/** * Adapter to InputStream. Just useful for quick test cases, no benefit over plain InputStream ;) */public class UncachedInputStreamSource implements InputStreamSource{    private InputStream m_input;    public UncachedInputStreamSource( InputStream input )    {        m_input = input;    }    public InputStream get()        throws IOException    {        return m_input;    }}