package org.ops4j.pax.repository.base;import java.io.InputStream;import com.google.inject.AbstractModule;import com.google.inject.TypeLiteral;import org.ops4j.pax.repository.spi.ArtifactEntry;import org.ops4j.pax.repository.spi.ArtifactFilter;import org.ops4j.pax.repository.spi.EntryParser;import org.ops4j.store.Store;import org.ops4j.store.StoreFactory;/** * */public class BaseModule extends AbstractModule{    @Override    protected void configure()    {        bind(new TypeLiteral<ArtifactFilter<ArtifactEntry>>() {} ).toInstance( new EmptyFilter(true) );        bind(new TypeLiteral<EntryParser<ArtifactEntry>>() {} ).to( DefaultEntryParser.class );        bind(new TypeLiteral<Store<InputStream>>() {} ).toInstance( StoreFactory.defaultStore() );    }}