package org.ops4j.pax.repository.base;import javax.inject.Inject;import org.ops4j.pax.repository.Artifact;import org.ops4j.pax.repository.Index;import org.ops4j.pax.repository.RepositoryException;import org.ops4j.pax.repository.Resolver;/** * Resolver implementation that just delegates queries to the index. */public class PassiveResolver<Q,T> implements Resolver<Q> {    private Index<Q, T> m_index;    @Inject    PassiveResolver( Index<Q, T> index )    {        m_index = index;    }    public Artifact find( Q query )        throws RepositoryException    {        return m_index.get( query );    }}