package org.ops4j.pax.repository.base;import java.io.InputStream;import javax.inject.Scope;import javax.inject.Singleton;import com.google.inject.AbstractModule;import com.google.inject.Provides;import com.google.inject.TypeLiteral;import org.ops4j.pax.repository.spi.ArtifactEntry;import org.ops4j.pax.repository.spi.ArtifactFilter;import org.ops4j.pax.repository.spi.EntryParser;import org.ops4j.store.Store;import org.ops4j.store.StoreFactory;/** * */public class RepositoryBaseModule extends AbstractModule{    @Override    protected void configure()    {        bind(new TypeLiteral<EntryParser<ArtifactEntry>>() {} ).to( DefaultEntryParser.class ).asEagerSingleton();    }    @Provides    @Singleton    private ArtifactFilter<ArtifactEntry> filter() {        return new EmptyFilter<ArtifactEntry>(true);    }    @Provides    @Singleton    private Store<InputStream> store() {        return StoreFactory.defaultStore();    }}