package org.ops4j.pax.repository.base;import org.ops4j.pax.repository.Artifact;/** * Helper for repository implementations. */public class RepositoryEntry<T>{    final private T m_key;    final private Artifact m_artifact;    public static <T> RepositoryEntry<T> createEntry( T key, Artifact artifact )    {        return new RepositoryEntry<T>( key, artifact );    }    private RepositoryEntry( T key, Artifact artifact )    {        m_key = key;        m_artifact = artifact;    }    public T getKey()    {        return m_key;    }    public Artifact getArtifact()    {        return m_artifact;    }}