package org.ops4j.pax.repository.base;import javax.inject.Inject;import javax.inject.Singleton;import org.ops4j.pax.repository.Index;import org.ops4j.pax.repository.RepositoryException;import org.ops4j.pax.repository.spi.Repository;/** * This is a helper component to make sure an index is filled with data. */@Singletonpublic class SyncService<T, Q>{    @Inject    Repository<T> m_repo;    @Inject    Index<Q, T> m_index;    public void syncNow()        throws RepositoryException    {        m_index.sync( m_repo );    }}