package org.ops4j.pax.repository.base.typed;import org.ops4j.pax.repository.typed.TypedReference;/** * */public class DefaultTypeMapping {    public static <T> TypeMapping map( final TypedReference typed, final T untyped) {        return new TypeMapping() {            public T untyped()            {                return untyped;            }            public TypedReference typed()            {                return typed;            }        };    }}