package org.ops4j.pax.repository.guice;import java.io.InputStream;import com.google.inject.AbstractModule;import com.google.inject.TypeLiteral;import org.ops4j.pax.repository.Index;import org.ops4j.pax.repository.Mapping;import org.ops4j.pax.repository.maven.GAV;import org.ops4j.pax.repository.maven.impl.GavIndex;import org.ops4j.pax.repository.maven.impl.StringToGAVMapping;/** * Guice Module for Maven specific implementations. */public class MavenModule extends AbstractModule{    @Override    protected void configure()    {        bind(new TypeLiteral<Index< GAV,GAV,InputStream>>(){ } ).to(new TypeLiteral<GavIndex<InputStream>>() {} );        bind(new TypeLiteral<Mapping<String,GAV>>(){ } ).to(new TypeLiteral<StringToGAVMapping>() {} );    }}